import axios from "axios";
import { useEffect, useState } from "react";
import "./Services.css";

export default function Services() {
  const [services, setServices] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/services")
      .then(res => setServices(res.data));
  }, []);

  const bookService = async (id) => {
    const dateTime = prompt("Enter date & time (YYYY-MM-DD HH:mm)");
    if (!dateTime) return;

    await axios.post(
      "http://localhost:5000/api/bookings",
      { serviceId: id, dateTime },
      {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token")
        }
      }
    );

    alert("✨ Booking Confirmed (Pending)");
  };

  return (
    <div className="services-container">
      <h1 className="services-title">Premium Salon Services</h1>

      <div className="services-grid">
        {services.map(service => (
          <div className="service-card" key={service._id}>
            <div className="service-name">{service.name}</div>
            <div className="service-price">₹ {service.price}</div>

            <button
              className="book-btn"
              onClick={() => bookService(service._id)}
            >
              Book Now
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
